//         });
//     };

//     const { getRootProps, getInputProps } = useDropzone({ onDrop });

//     return (
//         <div {...getRootProps()}>
//             <input {...getInputProps()} />
//             <AddIcon size="48" />
//         </div>
//     );
// }

function Drop({ channelName }) {
    const [uploadFile] = useMutation(CREATE_FILE);

    return (
        <Dropzone
            onDrop={(acceptedFiles) =>
                uploadFile({
                    variables: {
                        acceptedFiles,
                    },
                })
            }
        >
            {({ getRootProps, getInputProps }) => (
                <section>
                    <div {...getRootProps()}>
                        <input {...getInputProps()} />
                        <AddIcon size="48" />
                    </div>
                </section>
            )}
        </Dropzone>
    );
}

export default Drop;

const AddIcon = styled(AddCircle)``;

const CREATE_FILE_MESSAGE = gql`
    mutation CreateFileMessage($channelName: String!, $file: Upload!) {
        createFileMessage(input: { channelName: $channelName, file: $file })
    }
`;

const CREATE_FILE = gql`
    mutation CreateFile($file: Upload!) {
        createFile(picture: { file: $file })
    }
`;
