import React from "react";
import { Formik, Form } from "formik";
import { gql, useMutation } from "@apollo/client";
import {
    Input,
    Button,
    Box,
    Text,
    Alert,
    AlertIcon,
    FormControl,
    FormLabel,
} from "@chakra-ui/react";

//onSubmit={(values, { setSubmitting }) => createUser({ variables: values })}

const CREATE_USER = gql`
    mutation createUser($username: String!, $password: String!) {
        createUser(options: { username: $username, password: $password }) {
            user {
                id
                username
            }
            errors {
                message
            }
        }
    }
`;

export default function Register() {
    const [createUser, { data }] = useMutation(CREATE_USER);

    return (
        <div>
            <Box mt={8} mx="auto" maxW="400px" w="100%">
                <Text fontSize="50px" color="#4299E1">
                    Register
                </Text>

                <Formik
                    initialValues={{ username: "", password: "" }}
                    onSubmit={(values) => createUser({ variables: values })}
                >
                    <Form>
                        <FormControl>
                            <FormLabel width="4.5rem" htmlFor="username">
                                User Name
                            </FormLabel>
                            <Input
                                id="username"
                                name="username"
                                placeholder="username"
                                minLength="3"
                                isInvalid={!!err}
                                errorBorderColor="red.300"
                                ref={register}
                            />
                        </FormControl>

                        <FormControl>
                            <FormLabel width="4.5rem" htmlFor="password">
                                password
                            </FormLabel>
                            <Input
                                id="password"
                                name="password"
                                placeholder="username"
                                minLength="3"
                                isInvalid={!!err}
                                errorBorderColor="red.300"
                                ref={register}
                            />
                        </FormControl>

                        <Button type="submit"> Submit </Button>
                        {!!err ? (
                            <Alert status="error">
                                <AlertIcon />
                                {err}
                            </Alert>
                        ) : null}
                    </Form>
                </Formik>
            </Box>
        </div>
    );
}
