import React from "react";
import styled from "styled-components";
import Message from "./Message";
import { gql, useQuery } from "@apollo/client";

function Messages({ channelName }) {
    const { subscribeToMore, loading, data } = useQuery(MESSAGES, {
        variables: { channelName },
    });

    const MS = loading ? (
        <Container></Container>
    ) : (
        <Container>
            {/* {data.messages.map((message) => (
                <Message
                    timestamp={message.date}
                    message={message.text}
                    user={message.user}
                />
            ))} */}

            {subscribeToMore({
                document: NEW_MESSAGE,
                VARIABLES: { variables: { channelName } },
                updateQuery: (prev, { subscriptionData }) => {
                    if (!subscriptionData.data) console.log(prev);
                    const newFeedItem = subscriptionData.data.newMessage;
                    console.log(
                        Object.assign({}, prev, {
                            messages: [newFeedItem, ...prev.messages],
                        })
                    );
                },
            })}
        </Container>
    );

    return MS;
}
export default Messages;

const NEW_MESSAGE = gql`
    subscription new_Message($channelName: String!) {
        newMessage(channelName: $channelName) {
            user
            text
            date
        }
    }
`;

const MESSAGES = gql`
    query Messages($channelName: String!) {
        messages(channelName: $channelName) {
            user
            text
            date
        }
    }
`;

const Container = styled.div`
    flex: 1;
    overflow: auto;

    ::-webkit-scrollbar {
        width: 10px;
    }
    ::-webkit-scrollbar-thumb {
        background-color: #2f3542;
    }
    ::-webkit-scrollbar-track {
        background-color: grey;
    }
`;
