import React from "react";
import styled from "styled-components";
import { useForm } from "react-hook-form";
import { gql, useMutation } from "@apollo/client";
// import Message from "./components/Message";
import { AddCircle } from "@styled-icons/fluentui-system-regular/AddCircle";
// import { CardGiftcard } from "@styled-icons/material/CardGiftcard";
// import { Gif } from "@styled-icons/fluentui-system-regular/Gif";
// import { EmojiSmile } from "@styled-icons/bootstrap/EmojiSmile";

function Chat({ channelName }) {
    const { register, handleSubmit } = useForm();
    const [messageMutation] = useMutation(CREATE_MESSAGE);

    const onSubmit = async (data) =>
        await messageMutation({
            variables: { channelName: channelName, text: data },
        });

    return (
        <Container>
            <Messages>
                {/* {messages.map((message) => (
                    <Message
                        timestamp={message.createdAt}
                        message={message.text}
                        user={message.user}
                    />
                ))} */}
            </Messages>
            <MessagesInput>
                <AddIcon size="48" />
                <form onSubmit={handleSubmit(onSubmit)}>
                    <input
                        value={input}
                        disabled={!channelName}
                        {...register("text")}
                        // onChange={(e) => setInput(e.target.value)}
                        // placeholder={`Message #${channelName}`}
                    />
                    <button
                        disabled={!channelName}
                        type="submit"
                        // onClick={sendMessage}
                    >
                        Send
                    </button>
                </form>

                {/* <ChatIcons>
                    <Card size="40" />
                    <GifIcon size="40" />
                    <Emoji size="40" />
                </ChatIcons> */}
            </MessagesInput>
        </Container>
    );
}

export default Chat;

const CREATE_MESSAGE = gql`
    mutation createMessage($channelName: String!, $text: String!) {
        login(input: { channelName: $channelName, text: $text })
    }
`;
const Container = styled.div`
    display: flex;
    flex-direction: column;
    flex: 0.8;
    background-color: #363a3f;
    height: 100vh;
`;

const Messages = styled.div`
    flex: 1;
    overflow: auto;

    ::-webkit-scrollbar {
        width: 10px;
    }
    ::-webkit-scrollbar-thumb {
        background-color: #2f3542;
    }
    ::-webkit-scrollbar-track {
        background-color: grey;
    }
`;

const AddIcon = styled(AddCircle)``;

// const ChatIcons = styled.div``;

const MessagesInput = styled.div`
    color: lightgray;
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 15px;
    border-radius: 5px;
    margin: 20px;
    border-top: 1px solid gray;
    background-color: #474b53;

    form {
        flex: 1;
    }

    form > input {
        padding: 15px;
        background: transparent;
        border: none;
        outline-width: 0;
        color: white;
        font-size: large;
        width: 100%;
    }

    form > button {
        display: none;
    }
`;

