import React from "react";
import { Search as SearchIcon } from "@styled-icons/boxicons-regular/Search";
import styled from "styled-components";

function ChatHeader({ channelName }) {
    return (
        <Container>
            <HeaderLeft>
                <ChatHeaderHash>
                    <h3>
                        <ChatHeaderHash>#</ChatHeaderHash>
                        {channelName}
                    </h3>
                </ChatHeaderHash>
            </HeaderLeft>
            <HeaderRight>
                <Search>
                    <input placeholder="Search Channels" />
                    <Search_Icon size="20" />
                </Search>
            </HeaderRight>
        </Container>
    );
}

export default ChatHeader;

const Container = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    color: gray;
    padding: 10px;
`;

const HeaderLeft = styled.div``;

const ChatHeaderHash = styled.div`
    color: gray;
    font-size: 30px;
    padding: 10px;

    h3 {
        display: flex;
        align-items: center;
        color: white;
    }
`;

const HeaderRight = styled.div`
    display: flex;
    align-items: center;
    flex: 0.5;
    justify-content: space-between;
`;

const Search = styled.div`
    display: flex;
    align-items: center;
    color: gray;
    background-color: #2f3135;
    border-radius: 3px;
    padding: 3px;

    input {
        background: transparent;
        outline-width: 0;
        color: white;
        border: none;
    }
`;

const Search_Icon = styled(SearchIcon)`
    padding: 5px;
    cursor: pointer;
    &:hover {
        color: red;
    }
`;
