import React, { useEffect, useState } from "react";
import {
    Form,
    Message,
    Button,
    Input,
    Container,
    Header,
} from "semantic-ui-react";
import { gql, useMutation } from "@apollo/client";

const CREATE_USER = gql`
    mutation createUser(
        $username: String!
        $email: String!
        $password: String!
    ) {
        createUser(
            options: { username: $username, email: $email, password: $password }
        ) {
            user {
                id
                username
                email
            }
            errors {
                message
            }
        }
    }
`;

function Register() {
    const [values, setContent] = useState({ username: "", password: "" });
    const [createUser] = useMutation(CREATE_USER, {
        onCompleted() {
            console.log(" createUser ");
        },
    });

    const handleClick = () => {
        addNote({ variables: { content } });
        setContent("");
    };
}

export default Register;
