import React, { useState } from "react";
import { useForm } from "react-hook-form";
import { gql, useMutation } from "@apollo/client";
import {
    Input,
    InputGroup,
    InputLeftAddon,
    Button,
    Box,
    Text,
    Alert,
    AlertIcon,
} from "@chakra-ui/react";

const CREATE_USER = gql`
    mutation createUser($username: String!, $password: String!) {
        createUser(options: { username: $username, password: $password }) {
            user {
                id
                username
            }
            errors {
                message
            }
        }
    }
`;

export default function Register({ history }) {
    const [err, setErr] = useState("");

    const { register, handleSubmit } = useForm();
    const [createUser] = useMutation(CREATE_USER, {
        onCompleted({ createUser: { user, errors } }) {
            user ? history.push("/") : setErr(errors[0].message);
        },
    });

    const onSubmit = async (input) => {
        await createUser({ variables: input });
    };

    return (
        <form onSubmit={handleSubmit(onSubmit)}>
            <Box width="30%" display="grid" gridGap={2} mx="auto">
                <Text fontSize="50px" color="#4299E1">
                    Register
                </Text>
                <InputGroup>
                    <InputLeftAddon children="name" width="4.5rem" />
                    <Input
                        name="username"
                        placeholder="username"
                        minLength="3"
                        isInvalid={!!err}
                        errorBorderColor="red.300"
                        ref={register}
                    />
                </InputGroup>
                <InputGroup>
                    <InputLeftAddon children="email" width="4.5rem" />
                    <Input
                        name="email"
                        placeholder="Email"
                        ref={register}
                        pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,3}$"
                    />
                </InputGroup>
                <InputGroup>
                    <InputLeftAddon children="pw" width="4.5rem" />
                    <Input
                        name="password"
                        type="password"
                        minLength="3"
                        placeholder="Password"
                        ref={register}
                    />
                </InputGroup>
                <Button type="submit"> Submit </Button>
                {!!err ? (
                    <Alert status="error">
                        <AlertIcon />
                        {err}
                    </Alert>
                ) : null}
            </Box>
        </form>
    );
}
