import React from "react";
import styled from "styled-components";
import { ExpandMore } from "@styled-icons/material/ExpandMore";
import { Add } from "@styled-icons/fluentui-system-filled/Add";

const ALLCHANNEL = gql`
    query Allchannel {
        allChannel {
            name
        }
    }
`;

function Sidebar() {
    const { loading, error, data } = useQuery(ALLCHANNEL);
    if (loading) return "Loading...";
    if (error) return `Error! ${error.message}`;
    console.log(data);

    return (
        <Container>
            <SidebarTop>
                <h3> sidebar top </h3>
                <ExpandMore size="28" />
            </SidebarTop>
            <Channels>
                <ChannelsHeader>
                    <SidebarHeader>
                        <ExpandMore />
                        <h4>SidebarHeader</h4>
                    </SidebarHeader>

                    <AddIcon onClick={handleAddChannel} size="28" />
                </ChannelsHeader>

                <ChannelsList>
                    {/* {channels.map(({ id, channel }) => (
                        <SidebarChannel
                            key={id}
                            id={id}
                            channelName={channel.channelName}
                        >
                            <h4>
                                <ChannelHash>#</ChannelHash> {channelName}
                            </h4>
                        </SidebarChannel>
                    ))} */}
                </ChannelsList>
            </Channels>
        </Container>
    );
}

export default Sidebar;

const ChannelHash = styled.span`
    font-size: 30px;
    padding: 8px;
`;

const SidebarChannel = styled.div`
    &:hover > h4 {
        color: white;
        background-color: #40464b;
    }

    h4 {
        display: flex;
        padding-left: 15px;
        align-items: center;
        background-color: #2f3135;
        color: gray;
        cursor: pointer;
    }
`;

const AddIcon = styled(Add)`
    cursor: pointer;
    &: hover {
        color: white;
    }
`;

const Container = styled.div`
    display: flex;
    flex-direction: column;
    flex: 0.25;
    height: 100vh;
    background-color: #2f3135;
`;

const SidebarTop = styled.div`
    display: flex;
    justify-content: space-between;
    align-item: center;
    padding: 20px;
    background-color: #2f3135;
    color: white;
    border-bottom: 3px solid #26282c;
`;

const Channels = styled.div`
    flex: 1;
    overflow: auto;
    ::-webkit-scrollbar {
        width: 5px;
    }
    ::-webkit-scrollbar-thumb {
        background-color: #2f3542;
    }
    ::-webkit-scrollbar-track {
        background-color: grey;
    }
`;

const ChannelsHeader = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 10px;
    background-color: #2f3135;
    color: gray;
`;

const SidebarHeader = styled.div`
    display: flex;
    align-item: center;
`;

const ChannelsList = styled.div``;
